import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Pour la redirection
import axios from 'axios';
import './Login.css'; // Assurez-vous que ce fichier contient le style nécessaire

const LoginProvider = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false); // État de chargement
  const navigate = useNavigate(); // Pour rediriger après connexion

  const handleEmailLogin = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setError('Please fill out all fields.');
      return;
    }

    setLoading(true); // Afficher un indicateur de chargement
    setError('');
    setMessage('');

    try {
      const response = await axios.post('http://127.0.0.1:5000/provider/login', {
        email,
        password,
      });

      setMessage(response.data.message);
      setError('');
      setLoading(false);

      // Rediriger vers le tableau de bord du Provider
      navigate('/provider-dashboard');
    } catch (err) {
      setLoading(false);
      setError(err.response?.data?.error || 'Login failed. Please try again.');
      setMessage('');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1>Provider Login</h1>
        <form onSubmit={handleEmailLogin}>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>
        {message && <p className="success-message">{message}</p>}
        {error && <p className="error-message">{error}</p>}
      </div>
    </div>
  );
};

export default LoginProvider;
