import React, { useEffect, useState } from 'react';

const Profile = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Récupérer les informations utilisateur depuis le backend Flask
    fetch('http://127.0.0.1:5000/profile', { credentials: 'include' })
      .then(response => response.json())
      .then(data => setUser(data))
      .catch(error => console.error('Erreur lors de la récupération des données utilisateur:', error));
  }, []);

  if (!user) {
    return <p>Chargement...</p>;
  }

  return (
    <div>
      <h1>Bienvenue, {user.name}!</h1>
      <p>Email : {user.email}</p>
    </div>
  );
};

export default Profile;
