import React, { useState } from 'react';

const CLIENT_ID = "16844726883-nktuvt7v0fvoua9h948nvvl5ljddau9p.apps.googleusercontent.com";
const SCOPES = "https://www.googleapis.com/auth/calendar.events https://www.googleapis.com/auth/calendar"; // Assurez-vous d'avoir les bons scopes

const Login = () => {
  const [error, setError] = useState('');

  // Fonction de gestion de la connexion via Google
  const handleGoogleLogin = () => {
    const tokenClient = google.accounts.oauth2.initTokenClient({
      client_id: CLIENT_ID,
      scope: SCOPES,
      callback: (response) => {
        if (response.access_token) {
          // Stocker le token dans localStorage pour l'utiliser plus tard
          localStorage.setItem("googleToken", response.access_token);
          // Rediriger vers la page du calendrier après la connexion réussie
          window.location.href = "http://127.0.0.1:5000/calendar"; // Assurez-vous de rediriger vers l'URL du calendrier Flask
        } else {
          setError("Erreur lors de l'obtention du token Google.");
        }
      },
    });
    tokenClient.requestAccessToken();
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <p className="error-message">{error}</p>}
      <button onClick={handleGoogleLogin}>Login with Google</button>
    </div>
  );
};

export default Login;
