import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  const [error, setError] = useState('');

  // Fonction de gestion de la connexion via Google
  const handleGoogleLogin = () => {
    // Redirection vers le backend Flask pour commencer l'authentification Google
    axios.get('http://127.0.0.1:5000/login').then((response) => {
      const { token } = response.data; // Supposons que le backend renvoie un jeton.
      if (token) {
        localStorage.setItem('googleToken', token); // Stocker le jeton dans localStorage
        window.location.href = '/calendar'; // Rediriger vers le calendrier
      } else {
        setError('Échec de la connexion Google.');
      }
    }).catch((err) => {
      console.error(err);
      setError('Erreur lors de la connexion.');
    });
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <p className="error-message">{error}</p>}
      <button onClick={handleGoogleLogin}>Login with Google</button>
    </div>
  );
};

export default Login;
