import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Login = () => {
  const [error, setError] = useState('');

  const CLIENT_ID = "16844726883-nktuvt7v0fvoua9h948nvvl5ljddau9p.apps.googleusercontent.com"; // Remplacez par votre client ID
  const REDIRECT_URI = "http://127.0.0.1:5000/auth"; // Les permissions que vous voulez demander

  useEffect(() => {
    // Vérifiez si la bibliothèque google est chargée avant d'utiliser la méthode
    if (typeof google !== "undefined") {
      console.log("Google API loaded successfully.");
    } else {
      console.log("Google API not loaded.");
    }
  }, []);

  // Fonction de gestion de la connexion via Google
  const handleGoogleLogin = () => {
    if (typeof google !== "undefined") {
      const tokenClient = google.accounts.oauth2.initTokenClient({
        client_id: CLIENT_ID,
        redirect_uri: REDIRECT_URI,
        callback: (response) => {
          if (response.access_token) {
            // Stocker le token dans localStorage
            localStorage.setItem("googleToken", response.access_token);
            window.location.href = "/calendar"; // Rediriger vers la page du calendrier
          } else {
            setError("Erreur lors de l'obtention du token Google.");
          }
        },
      });

      tokenClient.requestAccessToken();
    } else {
      setError("Google API n'est pas chargée correctement.");
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <p className="error-message">{error}</p>}
      <button onClick={handleGoogleLogin}>Login with Google</button>
    </div>
  );
};

export default Login;
