import React, { useState, useEffect } from 'react';

const CLIENT_ID = "16844726883-nktuvt7v0fvoua9h948nvvl5ljddau9p.apps.googleusercontent.com";
const SCOPES = "https://www.googleapis.com/auth/calendar.events https://www.googleapis.com/auth/calendar"; 

const Login = () => {
  const [error, setError] = useState('');

  useEffect(() => {
    // Vérifiez si le script Google est chargé
    if (window.google && window.google.accounts) {
      console.log('Google API loaded successfully.');
    } else {
      console.error('Google API not loaded.');
    }
  }, []);

  // Fonction de gestion de la connexion via Google
  const handleGoogleLogin = () => {
    if (window.google && window.google.accounts) {
      const tokenClient = google.accounts.oauth2.initTokenClient({
        client_id: CLIENT_ID,
        scope: SCOPES,
        callback: (response) => {
          if (response.access_token) {
            localStorage.setItem("googleToken", response.access_token);
            window.location.href = "http://127.0.0.1:5000/auth";
          } else {
            setError("Erreur lors de l'obtention du token Google.");
          }
        },
      });
      tokenClient.requestAccessToken();
    } else {
      setError("Erreur: Google API non chargée.");
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <p className="error-message">{error}</p>}
      <button onClick={handleGoogleLogin}>Login with Google</button>
    </div>
  );
};

export default Login;
