import React, { useState } from 'react';

const CLIENT_ID = "16844726883-nktuvt7v0fvoua9h948nvvl5ljddau9p.apps.googleusercontent.com";
const SCOPES = "https://www.googleapis.com/auth/calendar.events https://www.googleapis.com/auth/calendar"; // Assurez-vous d'avoir les bons scopes

const Login = () => {
  const [error, setError] = useState('');

  // Fonction de gestion de la connexion via Google
  const handleGoogleLogin = () => {
    // Vérifiez si la bibliothèque google est bien chargée
    if (window.google) {
      const tokenClient = google.accounts.oauth2.initTokenClient({
        client_id: CLIENT_ID,
        scope: SCOPES,
        callback: (response) => {
          if (response.access_token) {
            // Stocker le token dans localStorage
            localStorage.setItem("googleToken", response.access_token);
            // Rediriger vers Flask pour gérer l'authentification
            window.location.href = "http://127.0.0.1:5000/auth"; // URL correcte de Flask
          } else {
            setError("Erreur lors de l'obtention du token Google.");
          }
        },
      });
      tokenClient.requestAccessToken();
    } else {
      // Afficher une erreur si la bibliothèque n'est pas chargée
      setError("Le script Google OAuth n'a pas été chargé correctement.");
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <p className="error-message">{error}</p>}
      <button onClick={handleGoogleLogin}>Login with Google</button>
    </div>
  );
};

export default Login;
