import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './Login';
import CalendarComponent from './CalendarComponent'; // Composant du calendrier
import './App.css';

function App() {
  const [token, setToken] = useState(null);

  // Gestion de la connexion réussie
  const handleLoginSuccess = (accessToken) => {
    setToken(accessToken); // Stocker le token après connexion
  };

  return (
    <Router>
      <div className="App">
        <header>
          <h1>Bienvenue dans le système de réservation</h1>
        </header>
        <main>
          <Routes>
            {/* Page de connexion */}
            <Route
              path="/"
              element={token ? <Navigate to="/calendar" /> : <Login onLoginSuccess={handleLoginSuccess} />}
            />

            {/* Page du calendrier */}
            <Route
              path="/calendar"
              element={token ? <CalendarComponent token={token} /> : <Navigate to="/" />}
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
