import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Reserve = () => {
  const [rooms, setRooms] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Récupérer les informations utilisateur et les salles depuis Flask
    axios.get('http://127.0.0.1:5000/reserve', { withCredentials: true })
      .then(response => {
        setUser(response.data.user);
        setRooms(response.data.rooms);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données :', error);
      });
  }, []);

  const handleReserve = (roomId) => {
    // Envoyer une demande de réservation au backend Flask
    axios.post('http://127.0.0.1:5000/reservations', { room_id: roomId }, { withCredentials: true })
      .then(response => {
        alert('Réservation réussie!');
      })
      .catch(error => {
        console.error('Erreur lors de la réservation :', error);
        alert('Erreur lors de la réservation.');
      });
  };

  if (!user) {
    return <p>Chargement...</p>;
  }

  return (
    <div>
      <h1>Bienvenue, {user.name}</h1>
      <p>Votre email : {user.email}</p>
      <h2>Salles disponibles</h2>
      <ul>
        {rooms.map(room => (
          <li key={room.id}>
            {room.name} - Capacité : {room.capacity} - Étage : {room.floor}
            <button onClick={() => handleReserve(room.id)}>Réserver</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Reserve;
